'use strict';

import { Form, Button, Input } from 'antd';
import FormArr from './index';

const FormItem = Form.Item;

class EditForm extends React.Component {
  save = () => {
    this.props.form.validateFieldsAndScroll((err, values) => {
      if (!err) {
        console.log('values: ', values);
      }
    });
  }

  cancel = () => {
    this.props.onCancel();
  }

  render() {
    const data = this.props.data || {};
    const { getFieldDecorator } = this.props.form;
    const formItemLayout = {
      labelCol: { span: 5 },
      wrapperCol: { span: 15 }
    };

    return (
      <Form className="short-link-long-editformComponent">
        <FormArr
          form={this.props.form}
          model="name"
        >
          <div>
            <Input style={{width: 300, marginLeft: '20.8%', marginBottom: 20}} />
          </div>
        </FormArr>

        <div className="modal-footer">
          <Button type="primary" size="large" onClick={this.save}>保存</Button>
          <Button size="large" style={{ marginLeft: 16 }} onClick={this.cancel}>取消</Button>
        </div>
      </Form>
    );
  }
}

EditForm.displayName = 'EditForm';
const WrappeEditForm = Form.create()(EditForm);
export default WrappeEditForm;

ReactDOM.render(<WrappeEditForm />, document.getElementById('app'));
